import requests

# Step 1: Upload data (assumes data is already uploaded)

# Step 2: Enable visualization features
settings_url = 'https://api-atlas.nomic.ai/v1/maps/settings'
headers = {
    'Authorization': 'Bearer <API_KEY>',
    'Content-Type': 'application/json'
}
settings_payload = {
    'settings': {
        'clustering': true,
        'filtering': true,
        'dimensionality_reduction': 'UMAP'
    }
}
response = requests.post(settings_url, headers=headers, json=settings_payload)
print(response.json())

