python_script.code:
import requests

# Step 1: Define the API endpoint and headers
url = 'https://api-atlas.nomic.ai/v1/maps/settings'
headers = {'Authorization': 'Bearer <API_KEY>', 'Content-Type': 'application/json'}

# Step 2: Define the settings payload
settings_payload = {
    'settings': {
        'color_scheme': 'viridis',
        'tooltips': {
            'fields': ['author', 'publication_date', 'category']
        },
        'clustering': true,
        'dimensionality_reduction': 'UMAP',
        'point_size': 5,
        'point_opacity': 0.6
    }
}

# Step 3: Apply the settings
response = requests.post(url, headers=headers, json=settings_payload)
print('Customization response:', response.json())
