import requests

url = "https://api-atlas.nomic.ai/v1/datasets/upload"

files = {
  'dataset_file': open('path/to/dataset.csv', 'rb')
}
data = {
  'dataset_name': 'My Dataset',
  'description': 'A sample dataset',
  'visibility': 'PUBLIC'
}
headers = {
  'Authorization': 'Bearer <TOKEN>'
}

response = requests.request("POST", url, headers=headers, files=files, data=data)

print(response.text)
